import React, { useState } from 'react';

const movies = [
  { id: 1, title: 'La La Land', img: 'https://m.media-amazon.com/images/M/MV5BMzUzNDM2NzM2MV5BMl5BanBnXkFtZTgwNTM3NTg4OTE@._V1_FMjpg_UX1000_.jpg' },
  { id: 2, title: 'Shakespeare in Love', img: 'https://upload.wikimedia.org/wikipedia/en/b/bd/Shakespeare_in_Love_%28soundtrack%29.jpg' },
  { id: 3, title: 'The Dark Knight', img: 'https://upload.wikimedia.org/wikipedia/en/1/1c/The_Dark_Knight_%282008_film%29.jpg' },
  { id: 4, title: 'F1: The Movie', img: 'https://m.media-amazon.com/images/S/pv-target-images/c93330e8ea31247d565cb78ed20369d0bd0f8ac77cc3fdcb617dd67d63c362b4.jpg' },
  { id: 5, title: 'Interstellar', img: 'https://m.media-amazon.com/images/I/91vIHsL-zjL._AC_UF1000,1000_QL80_.jpg' },
  { id: 6, title: 'The Prestige', img: 'https://images.unsplash.com/photo-1505686994434-e3cc5abf1330?w=300' },
  { id: 7, title: 'Memento', img: 'https://images.unsplash.com/photo-1485846234645-a62644f84728?w=300' },
  { id: 8, title: 'The Matrix', img: 'https://images.unsplash.com/photo-1626814026160-2237a95fc5a0?w=300' },
  { id: 9, title: 'Avatar', img: 'https://images.unsplash.com/photo-1518709268805-4e9042af9f23?w=300' },
  { id: 10, title: 'Joker', img: 'https://images.unsplash.com/photo-1594909122845-11baa439b7bf?w=300' },
  { id: 11, title: 'Avengers', img: 'https://images.unsplash.com/photo-1612036782180-6f0b6cd846fe?w=300' },
  { id: 12, title: 'Iron Man', img: 'https://images.unsplash.com/photo-1534809027769-b00d750a6bac?w=300' },

];

export default function App() {
  const [step, setStep] = useState(1);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [userData, setUserData] = useState({ name: '', email: '', mobile: '' });
  const [bookingId, setBookingId] = useState(null);


  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData(prev => ({ ...prev, [name]: value }));
  };


  const finalizeBooking = (e) => {
    e.preventDefault();
    setBookingId(Math.floor(Math.random() * 1000000));
    setStep(4);
  };

  return (
    <div style={{ maxWidth: '1000px', margin: '0 auto', padding: '20px', fontFamily: 'sans-serif' }}>


      {step === 1 && (
        <div>
          <h1>Movies List</h1>
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '15px' }}>
            {movies.map(movie => (
              <div key={movie.id} onClick={() => { setSelectedMovie(movie); setStep(2); }} style={{ border: '1px solid #ddd', cursor: 'pointer', padding: '10px', textAlign: 'center' }}>
                <img src={movie.img} alt={movie.title} style={{ width: '100%', height: '150px', objectFit: 'cover' }} />
                <h3>{movie.title}</h3>
              </div>
            ))}
          </div>
        </div>
      )}


      {step === 2 && selectedMovie && (
        <div style={{ textAlign: 'center' }}>
          <h1>{selectedMovie.title}</h1>
          <img src={selectedMovie.img} style={{ width: '400px', borderRadius: '10px' }} />
          <p>Explore the cinematic universe of {selectedMovie.title}.</p>
          <button onClick={() => setStep(3)} style={{ padding: '10px 20px', fontSize: '18px', cursor: 'pointer' }}>Book Seat</button>
          <br /><br />
          <button onClick={() => setStep(1)}>Back to Movies</button>
        </div>
      )}


      {step === 3 && (
        <div>
          <h1>Booking Details</h1>
          <form onSubmit={finalizeBooking} style={{ display: 'flex', flexDirection: 'column', gap: '15px', maxWidth: '400px' }}>
            <input name="name" placeholder="Full Name" required value={userData.name} onChange={handleInputChange} style={{ padding: '10px' }} />
            <input name="email" type="email" placeholder="Email Address" required value={userData.email} onChange={handleInputChange} style={{ padding: '10px' }} />
            <input name="mobile" type="tel" placeholder="Mobile Number" required value={userData.mobile} onChange={handleInputChange} style={{ padding: '10px' }} />
            <button type="submit" style={{ padding: '10px', background: 'blue', color: 'white', border: 'none', cursor: 'pointer' }}>Submit</button>
          </form>
        </div>
      )}


      {step === 4 && (
        <div style={{ textAlign: 'center', border: '2px solid green', padding: '30px', borderRadius: '15px' }}>
          <h1 style={{ color: 'green' }}>Seat Booked!</h1>
          <h3>Booking ID: {bookingId}</h3>
          <hr />
          <div style={{ textAlign: 'left', display: 'inline-block' }}>
            <p><strong>Movie:</strong> {selectedMovie.title}</p>
            <p><strong>Name:</strong> {userData.name}</p>
            <p><strong>Email:</strong> {userData.email}</p>
            <p><strong>Mobile:</strong> {userData.mobile}</p>
          </div>
          <br /><br />
          <button onClick={() => window.location.reload()} style={{ padding: '10px 20px' }}>Book Another Movie</button>
        </div>
      )}
    </div>
  );
}
